var indexActive = 0;
var animated = false;
var sirkaObrazku = 84;
$(window).load(function(){
	
	
	
   if ($('#dkz_minifoto_box').length) {
      
      
      sirkaOverview = 0;
      $('.dkz_foto').each(function(){
         sirkaOverview += sirkaObrazku;
      });
      $('.overview').css({'width': sirkaOverview + 'px'});

      
      scrollbar = sirkaOverview > parseInt($('.mini-photo').css('width'));

      
      if (!scrollbar) {
         $('.viewport').css({'width': sirkaOverview + 'px'});
      }
      
      
	   
      $('.dkz_foto a').click(function(){
         index = parseInt($(this).parent().attr('id').substr(8));
         zmenitFotku(index, false);
         return false;
      });

      // Scrollbar.
      if (scrollbar) {
         $('#dkz_minifoto_box').tinyscrollbar({axis: 'x'});
         $('#dkz_minifoto_box').hover(function(){
            $('#dkz_minifoto_box').tinyscrollbar_update('relative');
            $(this).find('.scrollbar').fadeIn();
         }, function() {
            $(this).find('.scrollbar').fadeOut();
         });
      }
      
   }
   
});

function zmenitFotku(index, swap) {
   index = parseInt(index);
   $('.dkz_foto_active').removeClass('dkz_foto_active');
   $('#dkz_foto' + index).addClass('dkz_foto_active');
   poziceOverview = parseInt($('.overview').css('left'));
   poziceFotky = $('#dkz_foto' + index).position().left + poziceOverview;
   sirkaFotky = $('#dkz_foto' + index).outerWidth(true);
   sirkaViewport = $('.viewport').width();
   if (poziceFotky + sirkaFotky > sirkaViewport) {
      animated = true;
      kolik = poziceFotky + sirkaFotky - sirkaViewport;
      $('.overview').animate(
         {left: '-=' + kolik},
         200,
         function(){
            animated = false;
            if (scrollbar) {
               $('#dkz_minifoto_box').tinyscrollbar_update(-parseInt($('.overview').css('left')));
            }
         }
      );
   } else if (poziceFotky < 0) {
      animated = true;
      kolik = -poziceFotky;
      $('.overview').animate(
         {left: '+=' + kolik},
         200,
         function(){
            animated = false;
            if (scrollbar) {
               $('#dkz_minifoto_box').tinyscrollbar_update(-parseInt($('.overview').css('left')));
            }
         }
      );
   }
   
   if (swap) {
      var fotkaMaxi = $('#dkz_foto' + index + ' a').attr('data-zoom-image');
      var fotkaNormal = $('#dkz_foto' + index + ' a').attr('data-image')
      $('#fotka').data('elevateZoom').swaptheimage(fotkaNormal, fotkaMaxi);
   }

         
   indexActive = index;
}

function inicializovatDkzZoom() {
   if ($('#fotka').length) {
      $('#fotka').elevateZoom({
         zoomType: 'inner',
         gallery: 'fotky',
         cursor: 'pointer',
         galleryActiveClass: 'active',
         imageCrossfade: true,
         loadingIcon: 'img/loadingAnimation.gif',
         zoomWindowFadeIn: 500,
         zoomWindowFadeOut: 500
      });
	   
	  

      $('#fotka').bind('click', function(e) {
         var ez = $(this).data('elevateZoom');
         var index = ez.getGalleryIndex();
         if (index != null) {
            $.fancybox(
               ez.getGalleryList(),
               {
                  index: index,
				   helpers : { 
					title : { type : 'outside' }
				   }, // helpers
				   afterLoad : function() {
					this.title = (this.title ? '' + this.title : '') + ' <span class="number-fancy-img">' + (this.index + 1) + '/' + this.group.length + ' </span>';
				   } // afterLoad

               }
            );
         };
		  $('body').addClass('without_zoom');
         return false;
      });
	   
	
   }
};

